mapper.update:
UPDATE ALBA 

	ALBA_NAME = #{alba_name}
	ALBA_PHONE = #{alba_phone}
	ALBA_ADDRESS = #{alba_address}
	ALBA_TIMESAL = #{alba_timesal}
	ALBA_BANK = #{alba_bank}
	ALBA_ACCOUNT=#{alba_account}

WHERE ALBA_SEQ = #{alba_seq}

mapper.select:
SELECT ALBA_SEQ, ALBA_NAME 
FROM ALBA 
WHERE STORE_CODE=#{store_code}
