mapper.select:
SELECT OWNER_SEQ, OWNER_ID, OWNER_NAME, OWNER_PHONE, OWNER_EMAIL, AUTH, STORE_CODE, OWNER_START, ADMIN_ID 
FROM OWNER
WHERE OWNER_ID= #{owner_id} AND OWNER_PW= #{owner_pw}

mapper.update:
UPDATE ADMIN 
SET ADMIN_PW = #{temp_pw} 
WHERE ADMIN_EMAIL = #{admin_email} AND ADMIN_ID = #{admin_id}

UPDATE OWNER 
SET OWNER_PW = #{temp_pw} 
WHERE OWNER_EMAIL = #{owner_email} AND OWNER_ID = #{owner_id}

UPDATE ADMIN 

	ADMIN_PW = #{admin_pw},
	ADMIN_NAME = #{admin_name},
	ADMIN_PHONE = #{admin_phone},
	ADMIN_EMAIL = #{admin_email}

WHERE ADMIN_ID = #{admin_id}

UPDATE OWNER

	OWNER_PW = #{owner_pw},
	OWNER_NAME = #{owner_name},
	OWNER_PHONE = #{owner_phone},
	OWNER_EMAIL = #{owner_email}

WHERE OWNER_SEQ = #{owner_seq}

mapper.select:
SELECT COUNT(ADMIN_ID) 
FROM ADMIN 
WHERE ADMIN_ID = #{admin_id}

SELECT COUNT(OWNER_ID) 
FROM OWNER 
WHERE OWNER_ID = #{owner_id}
