mapper.select:
SELECT FILE_SEQ, FILE_TNAME, FILE_RNAME, MENU_SEQ,MENU_NAME,MENU_PRICE,MENU_CATEGORY FROM MENU JOIN FILE_LIST USING (MENU_SEQ) WHERE MENU_SEQ IN
#{menu_seq}
AND MENU_CATEGORY = #{menu_category}
ORDER BY MENU_PRICE DESC

SELECT FILE_SEQ, FILE_TNAME, FILE_RNAME, MENU_SEQ,MENU_NAME,MENU_PRICE,MENU_CATEGORY,MENU_DELFLAG FROM MENU JOIN FILE_LIST USING (MENU_SEQ)
WHERE MENU_CATEGORY = #{menu_category}
ORDER BY MENU_PRICE DESC
