mapper.update:
UPDATE NOTICE
	SET NOTICE_TITLE=#{notice_title}, NOTICE_CONTENT=#{notice_content}
		WHERE NOTICE_SEQ=#{notice_seq}

UPDATE NOTICE
	SET NOTICE_DELFLAG='Y'
		WHERE NOTICE_SEQ=#{notice_seq}

mapper.select:
SELECT NOTICE_SEQ, NOTICE_TITLE, NOTICE_ID, TO_CHAR(NOTICE_REGDATE,'YYYY-MM-dd') AS NOTICE_REGDATE, NOTICE_CONTENT, NOTICE_DELFLAG
	FROM NOTICE
		WHERE NOTICE_SEQ = #{notice_seq}
