mapper.select:
SELECT REQUEST_SEQ,REQUEST_MENU, REQUEST_TIME,OS_CODE 
FROM REQUEST 
WHERE STORE_CODE =#{store_code}
AND OS_CODE=1 AND REQUEST_SEQ=#{request_seq}
ORDER BY REQUEST_TIME DESC

SELECT REQUEST_MENU,REQUSET_TIME,OS_CODE 
FROM REQUEST 
WHERE STORE_CODE = #{store_code} AND OS_CODE=1 AND REQUEST_SEQ=#{request_seq}

SELECT REQUEST_SEQ,REQUEST_MENU, REQUEST_TIME,OS_CODE 
FROM REQUEST 
WHERE STORE_CODE = #{store_code}  
AND OS_CODE=2 AND REQUEST_SEQ=#{request_seq}
ORDER BY REQUEST_TIME DESC

SELECT REQUEST_MENU,REQUSET_TIME,OS_CODE 
FROM REQUEST
WHERE STORE_CODE = #{store_code} AND OS_CODE=1 AND REQUEST_SEQ=#{request_seq}
