mapper.select:
	SELECT TS_SEQ, ALBA_SEQ, TS_DATE, TS_DATETIME, TS_WORKHOUR
		FROM TIMESHEET

			WHERE ALBA_SEQ = #{alba_seq}
			AND TS_DATE = #{ts_date}

	SELECT TS_DATETIME
		FROM TIMESHEET
			WHERE ALBA_SEQ = #{alba_seq}
			AND TS_DATE = #{ts_date}

mapper.delete:
DELETE FROM TIMESHEET
	WHERE ALBA_SEQ = #{alba_seq}
		AND TS_DATE = #{ts_date}
